package org.devbuild.algoservice.sort;import java.util.List;public interface SortService {    List<SortEl> sort(List<SortEl> nodes) throws InterruptedException;    void mergeSort(List<SortEl> nodes, int start , int end, long timeInMills) throws InterruptedException;    void populateSortElData(List<SortEl> nodes);    void maxHeap(List<SortEl> nodes, int i, int n) throws InterruptedException;    List<SortEl> heapSort(List<SortEl> nodes, long timeInMills) throws InterruptedException;    void bubbleSort(List<SortEl> nodes, long timeInMills) throws InterruptedException;}